select count(a.weibo_id) from thefair_weibo_data a left join thefair_wbuser b on a.uid = b.uid where b.tags like "%电竞%" or b.tags like "%创造力%" or b.tags like "%游戏%" or b.tags like "%想象力%" or b.tags like "%好奇心%" or b.tags like "%潮流时尚%" or b.tags like "%个性%" or b.tags like "%艺术%" or b.tags like "%创新%" or b.tags like "%跨界%" or b.tags like "%新奇%" or b.tags like "%科幻%" or b.tags like "%外星人%" or b.tags like "%gamer%" or b.tags like "%电竞%" or b.tags like "%虚拟%" or b.tags like "%宇宙%" or b.tags like "%NARS%" or b.tags like "%独特%" or b.tags like "%不一样%" or b.tags like "%酷%" or b.tags like "%电竞%" or b.tags like "%剪辑后期%" or b.tags like "%音乐创作%" or b.tags like "%程序员%" or b.tags like "%文字工作者%"\G;